<?php	if(isset($_POST['save']) && $_POST['save'] != "") {		plugin_kontakt::setOption("HTML_SHORT_TAG", $_POST['HTML_SHORT_TAG']);		plugin_kontakt::setOption("HTML_POSITION", $_POST['HTML_POSITION']);		plugin_kontakt::setOption("SPAM_PROTECTION_SHOW", $_POST['SPAM_PROTECTION_SHOW']);		plugin_kontakt::setOption("HTML_TEXT", $_POST['content']);		plugin_kontakt::setOption("BUTTON_SEND_NAME", $_POST['BUTTON_SEND_NAME']);		plugin_kontakt::setOption("BUTTON_RESET_SHOW", $_POST['BUTTON_RESET_SHOW']);		plugin_kontakt::setOption("BUTTON_RESET_NAME", $_POST['BUTTON_RESET_NAME']);				wp_redirect("options-general.php?page=plugin_kontakt&site=settings&notify=save", 200);		exit();	}		if(isset($_GET['notify']) && $_GET['notify'] != "") {		switch($_GET['notify']) {			case "save":				$info = "Die Einstellungen wurden erfolgreich gespeichert.";			break;			default:				$info = "";			break;		}				if(isset($info) && $info != "") {			print "<div class=\"updated below-h2\" id=\"message\"><p>" . $info . "</p></div><br />";		}	}?><div class="form_designer">	<div class="top">		<h2 class="np">Einstellungen</h2>	</div>	<form method="post" action="options-general.php?page=plugin_kontakt&site=settings">		<table class="wp-list-table widefat fixed pages">			<tr>				<td>HTML Short-Tags</td>				<td>Inhalt Position</td>			</tr>			<tr>				<td>					<select name="HTML_SHORT_TAG">						<option value="1"<?php print (plugin_kontakt::getOption("HTML_SHORT_TAG") == 1 ? " SELECTED" : ""); ?>>Ja</option>						<option value="0"<?php print (plugin_kontakt::getOption("HTML_SHORT_TAG") == 0 ? " SELECTED" : ""); ?>>Nein</option>					</select>					<p>						HTML-Shorttags können aktiviert werden und sind für eine Valide Seite zuständig (Wenn der Doctyp dies zulässt)					</p>				</td>				<td>					<select name="HTML_POSITION">						<option value="top"<?php print (plugin_kontakt::getOption("HTML_POSITION") == "top" ? " SELECTED" : ""); ?>>Oben</option>						<option value="bottom"<?php print (plugin_kontakt::getOption("HTML_POSITION") == "bottom" ? " SELECTED" : ""); ?>>Unten</option>						<option value="left"<?php print (plugin_kontakt::getOption("HTML_POSITION") == "left" ? " SELECTED" : ""); ?>>Links</option>						<option value="right"<?php print (plugin_kontakt::getOption("HTML_POSITION") == "right" ? " SELECTED" : ""); ?>>Rechts</option>					</select>					<p>						Hier können Sie das Kontaktformular gegenüber dem definierten Inhaltstext positionieren.					</p>				</td>			</tr>			<tr>				<td>Spam-Protection</td>				<td>Inhaltstext</td>			</tr>			<tr>				<td>					<select name="SPAM_PROTECTION_SHOW">						<option value="1"<?php print (plugin_kontakt::getOption("SPAM_PROTECTION_SHOW") == 1 ? " SELECTED" : ""); ?>>Aktivieren</option>						<option value="0"<?php print (plugin_kontakt::getOption("SPAM_PROTECTION_SHOW") == 0 ? " SELECTED" : ""); ?>>Deaktivieren</option>					</select>					<p>						Hier können Sie ein Captcha aktivieren.					</p>				</td>				<td rowspan="5">					<div id="poststuff" class="metabox-holder">						<div id="post-body">							<div id="post-body-content">								<?php									print '<div id="' . (user_can_richedit() ? 'postdivrich' : 'postdiv') . '" class="postarea_one">';									the_editor(stripslashes(plugin_kontakt::getOption("HTML_TEXT")), "content", "content_preview", true, 1);									print '</div>';								?>							</div>						</div>						<br class="clear" />					</div>					<p>						Hier können Sie den Inhaltstext im Kontaktformular definieren					</p>				</td>			</tr>			<tr>				<td>Senden-Button</td>			</tr>			<tr>				<td>					<input type="text" name="BUTTON_SEND_NAME" value="<?php print plugin_kontakt::getOption("BUTTON_SEND_NAME"); ?>" />				</td>			</tr>			<tr>				<td>Reset-Button</td>			</tr>			<tr>				<td>					<select name="BUTTON_RESET_SHOW">						<option value="1"<?php print (plugin_kontakt::getOption("BUTTON_RESET_SHOW") == 1 ? " SELECTED" : ""); ?>>Aktivieren</option>						<option value="0"<?php print (plugin_kontakt::getOption("BUTTON_RESET_SHOW") == 0 ? " SELECTED" : ""); ?>>Deaktivieren</option>					</select>					<input type="text" name="BUTTON_RESET_NAME" value="<?php print plugin_kontakt::getOption("BUTTON_RESET_NAME"); ?>" />				</td>			</tr>		</table>		<br />		<div style="float: right">			<input type="submit" value="Speichern" class="button-primary" id="submit" name="save">		</div>	</form></div>